package com.killercraft.jimy.Utils;public class CSUnicodeUtil {    public static String toUnicode(final String gbString) {        if (gbString.contains("#")) {            return gbString;        }        char[] utfBytes = gbString.toCharArray();        String unicodeBytes = "";        for (char utfByte : utfBytes) {            String hexB = Integer.toHexString(utfByte);            if (hexB.length() <= 2) {                hexB = "00" + hexB;            }            unicodeBytes = unicodeBytes + "#" + hexB;        }        return unicodeBytes;    }    public static String toWord(String dataStr) {        if (!dataStr.contains("#")) return dataStr;        dataStr = dataStr.replace("#", "\\u");        int start = 0;        int end = 0;        final StringBuilder buffer = new StringBuilder();        while (start > -1) {            end = dataStr.indexOf("\\u", start + 2);            String charStr = "";            if (end == -1) {                charStr = dataStr.substring(start + 2);            } else {                charStr = dataStr.substring(start + 2, end);            }            char letter = (char) Integer.parseInt(charStr, 16);            buffer.append(new Character(letter));            start = end;        }        return buffer.toString();    }}